'use strict';

function ownKeys(object, enumerableOnly) {
  var keys = Object.keys(object);
  if (Object.getOwnPropertySymbols) {
    var symbols = Object.getOwnPropertySymbols(object);
    if (enumerableOnly)
      symbols = symbols.filter(function(sym) {
        return Object.getOwnPropertyDescriptor(object, sym).enumerable;
      });
    keys.push.apply(keys, symbols);
  }
  return keys;
}

function _objectSpread(target) {
  for (var i = 1; i < arguments.length; i++) {
    var source = arguments[i] != null ? arguments[i] : {};
    if (i % 2) {
      ownKeys(Object(source), true).forEach(function(key) {
        _defineProperty(target, key, source[key]);
      });
    } else if (Object.getOwnPropertyDescriptors) {
      Object.defineProperties(target, Object.getOwnPropertyDescriptors(source));
    } else {
      ownKeys(Object(source)).forEach(function(key) {
        Object.defineProperty(
          target,
          key,
          Object.getOwnPropertyDescriptor(source, key),
        );
      });
    }
  }
  return target;
}

function _defineProperty(obj, key, value) {
  if (key in obj) {
    Object.defineProperty(obj, key, {
      value: value,
      enumerable: true,
      configurable: true,
      writable: true,
    });
  } else {
    obj[key] = value;
  }
  return obj;
}

const _require = require('../../Utils'),
  capitalize = _require.capitalize;

class PojoCollector {
  constructor() {
    _defineProperty(this, '_pojos', new Map());
  }

  _process(namespace, pojoName, typeAnnotation) {
    switch (typeAnnotation.type) {
      case 'ObjectTypeAnnotation': {
        this._insertPojo(namespace, pojoName, typeAnnotation, false);

        return {
          type: 'PojoTypeAliasTypeAnnotation',
          name: pojoName,
        };
      }

      case 'ArrayTypeAnnotation': {
        const arrayTypeAnnotation = typeAnnotation; // TODO: Flow assumes elementType can be any. Fix this.

        const elementType = arrayTypeAnnotation.elementType;

        const pojoElementType = (() => {
          switch (elementType.type) {
            case 'ObjectTypeAnnotation': {
              this._insertPojo(
                namespace,
                `${pojoName}Element`,
                elementType,
                false,
              );

              return {
                type: 'PojoTypeAliasTypeAnnotation',
                name: `${pojoName}Element`,
              };
            }

            case 'ArrayTypeAnnotation': {
              const objectTypeAnnotation = elementType.elementType;

              this._insertPojo(
                namespace,
                `${pojoName}ElementElement`,
                objectTypeAnnotation,
                false,
              );

              return {
                type: 'ArrayTypeAnnotation',
                elementType: {
                  type: 'PojoTypeAliasTypeAnnotation',
                  name: `${pojoName}ElementElement`,
                },
              };
            }

            default: {
              return elementType;
            }
          }
        })();

        return {
          type: 'ArrayTypeAnnotation',
          elementType: pojoElementType,
        };
      }

      default:
        return typeAnnotation;
    }
  }

  _insertPojo(namespace, pojoName, objectTypeAnnotation, isRoot) {
    const properties = objectTypeAnnotation.properties.map(property => {
      const propertyPojoName = pojoName + capitalize(property.name);
      return _objectSpread(
        _objectSpread({}, property),
        {},
        {
          typeAnnotation: this._process(
            namespace,
            propertyPojoName,
            property.typeAnnotation,
          ),
        },
      );
    });

    this._pojos.set(pojoName, {
      name: pojoName,
      isRoot,
      namespace,
      properties,
    });
  }

  processPojo(namespace, pojoName, objectTypeAnnotation) {
    this._insertPojo(namespace, pojoName, objectTypeAnnotation, true);
  }

  getAllPojos() {
    return [...this._pojos.values()];
  }
}

module.exports = PojoCollector;
